import { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { FaBars, FaTimes } from 'react-icons/fa';

const Navbar = () => {
  const [scrolled, setScrolled] = useState(false);
  const [mobileMenuOpen, setMobileMenuOpen] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      if (window.scrollY > 50) {
        setScrolled(true);
      } else {
        setScrolled(false);
      }
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  const toggleMobileMenu = () => {
    setMobileMenuOpen(!mobileMenuOpen);
  };

  const navLinks = [
    { path: "/", text: "الرئيسية" },
    { path: "/about", text: "من نحن" },
    { path: "/services", text: "الخدمات" },
    { path: "/contact", text: "اتصل بنا" }
  ];

  return (
    <nav
      className={`fixed top-0 right-0 w-full z-50 transition-all duration-500 ease-in-out ${
        scrolled ? 'bg-indigo-900 py-3 shadow-lg' : 'bg-indigo-700 py-4'
      }`}
      dir="rtl"
    >
      <div className="container mx-auto px-4 flex justify-between items-center">
        <Link to="/" className="text-white text-2xl font-bold hover:text-indigo-200 transition-colors">
          شعار الموقع
        </Link>

        {/* روابط سطح المكتب */}
        <ul className="hidden md:flex space-x-8 space-x-reverse">
          {navLinks.map((link) => (
            <li key={link.path}>
              <Link
                to={link.path}
                className="text-white hover:bg-white/20 px-4 py-2 rounded-lg transition-all duration-300"
              >
                {link.text}
              </Link>
            </li>
          ))}
        </ul>

        {/* زر القائمة المتنقلة */}
        <button
          className="md:hidden text-white text-2xl focus:outline-none"
          onClick={toggleMobileMenu}
        >
          {mobileMenuOpen ? <FaTimes /> : <FaBars />}
        </button>
      </div>

      {/* القائمة المتنقلة */}
      <div
        className={`md:hidden bg-indigo-800 transition-all duration-300 ease-in-out overflow-hidden ${
          mobileMenuOpen ? 'max-h-96 py-4' : 'max-h-0 py-0'
        }`}
      >
        <ul className="flex flex-col items-center space-y-4">
          {navLinks.map((link) => (
            <li key={link.path}>
              <Link
                to={link.path}
                className="text-white hover:bg-white/20 px-4 py-2 rounded-lg block w-full text-center"
                onClick={() => setMobileMenuOpen(false)}
              >
                {link.text}
              </Link>
            </li>
          ))}
        </ul>
      </div>
    </nav>
  );
};

export default Navbar;
