import React, { useState, useEffect } from 'react';
import { FaBars, FaSun, FaMoon, FaTimes } from 'react-icons/fa';
import ''

function NavBar() {
  // حالة الوضع الليلي
  const [darkMode, setDarkMode] = useState(() => {
    return JSON.parse(localStorage.getItem('darkMode')) ?? 
           window.matchMedia?.('(prefers-color-scheme: dark)').matches;
  });

  // حالة القائمة المنسدلة
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  // تطبيق الثيم على الصفحة
  useEffect(() => {
    document.documentElement.classList.toggle('dark-theme', darkMode);
    localStorage.setItem('darkMode', darkMode);
  }, [darkMode]);

  const toggleTheme = () => {
    setDarkMode(!darkMode);
  };

  const closeMenu = () => {
    setIsMenuOpen(false);
  };

  return (
    <nav className="navbar">
      {/* الجزء الأيسر (الشعار) */}
      <div className="navbar-left">
        <img src='/images/53.jpeg' alt="logo" className='logo' />
        <h1>zvshh</h1>
      </div>

      {/* زر القائمة (للجوال) */}
      <button 
        className="mobile-menu-button" 
        onClick={() => setIsMenuOpen(!isMenuOpen)}
        aria-label="Toggle menu"
      >
        {isMenuOpen ? <FaTimes /> : <FaBars />}
      </button>

      {/* الجزء الأيمن (الروابط + زر الثيم) */}
      <div className={`navbar-right ${isMenuOpen ? 'active' : ''}`}>
        <a href="https://github.com" target="_blank" rel="noopener noreferrer" onClick={closeMenu}>GitHub</a>
        <a href="https://twitter.com" target="_blank" rel="noopener noreferrer" onClick={closeMenu}>Twitter</a>
        <a href="https://instagram.com" target="_blank" rel="noopener noreferrer" onClick={closeMenu}>Instagram</a>

        <button 
          className="theme-toggle" 
          onClick={toggleTheme}
          aria-label={darkMode ? 'Switch to light mode' : 'Switch to dark mode'}
        >
          {darkMode ? <FaSun /> : <FaMoon />}
        </button>
      </div>
    </nav>
  );
}

export default NavBar;
