import React, { useState } from 'react';

function NavBar() {
  const [darkMode, setDarkMode] = useState(false);

  const toggleDarkMode = () => {
    setDarkMode(!darkMode);
  };

  // تطبيق الألوان حسب الوضع
  const navbarStyle = {
    backgroundColor: darkMode ? '#1a1a1a' : '#ffffff',
    color: darkMode ? '#ffffff' : '#000000'
  };

  const linkStyle = {
    color: darkMode ? '#ffffff' : '#000000'
  };

  return (
    <nav className='navbar' dir='rtl' style={navbarStyle}>
      {/* الجانب الأيمن - الروابط */}
      <div className='navbar-right'>
        <a href='https://github.com' target='_blank' rel='noopener noreferrer' style={linkStyle}>
          جيثب
        </a>
        <a href='https://twitter.com' target='_blank' rel='noopener noreferrer' style={linkStyle}>
          تويتر
        </a>
        <a href='https://instagram.com' target='_blank' rel='noopener noreferrer' style={linkStyle}>
          إنستغرام
        </a>

        {/* زر تغيير الثيم */}
        <button 
          className='theme-toggle' 
          onClick={toggleDarkMode}
          aria-label={darkMode ? 'تفعيل الوضع الفاتح' : 'تفعيل الوضع الداكن'}
          style={{
            background: darkMode ? '#fff' : '#000',
            color: darkMode ? '#000' : '#fff',
            border: 'none',
            padding: '8px 12px',
            borderRadius: '4px',
            cursor: 'pointer'
          }}
        >
          {darkMode ? '🌞' : '🌙'}
        </button>
      </div>

      {/* الجانب الأيسر - الشعار */}
      <div className='navbar-left'>
        <h1 style={{ color: darkMode ? '#ffffff' : '#000000' }}>زشه</h1>
        <img src="#" alt="شعار" />
      </div>
    </nav>
  );
}

export default NavBar;
